function script() {
  const targetDiv = document.getElementById("table1");
  const targetDiv1 = document.getElementById("table2");
  const targetDiv2 = document.getElementById("table3");
  const targetDiv3 = document.getElementById("table4");
  const btn1 = document.getElementById("btn1");
  const btn2 = document.getElementById("btn2");
  const btn3 = document.getElementById("btn3");
  const btn4 = document.getElementById("btn4");

  btn1.addEventListener("click", function handleClick() {
    console.log("btn 1 clicked");
  });

  function dispbtn() {
    if (btn1.onclick) {
      targetDiv1.style.display = "none";
      targetDiv2.style.display = "none";
      targetDiv3.style.display = "none";
    } else if (btn2.onclick) {
      targetDiv.style.display = "none";

      targetDiv2.style.display = "none";
      targetDiv3.style.display = "none";
    } else if (btn3.onclick) {
      targetDiv.style.display = "none";
      targetDiv1.style.display = "none";

      targetDiv3.style.display = "none";
    } else if (btn4.onclick) {
      targetDiv.style.display = "none";
      targetDiv1.style.display = "none";
      targetDiv2.style.display = "none";
    }
  }
}
export default script;
