import { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom';
import { NavLink } from 'react-router-dom';
import '../assets/css/nucleo-icons.css';
import '../assets/css/nucleo-svg.css';
import '../assets/css/textanimate.css';
import '../assets/css/argon-dashboard.css?v=2.0.4';
function Add() {

  const images = importAll(require.context('../assets/img/', false, /\.(png|jpe?g|svg)$/));

  function importAll(r) {
    let images = {};
    r.keys().map((item, index) => { images[item.replace('./', '')] = r(item); });
    return images;
  }
  const [getuserdata, setUserdata] = useState([]);
  console.log(getuserdata);

  // const { udata, setUdata } = useContext(adddata);

  // const {updata, setUPdata} = useContext(updatedata);

  // const {dltdata, setDLTdata} = useContext(deldata);

  const Navigate = useNavigate()
  const [name, setName] = useState('')
  const [email, setEmail] = useState('')
  const [city, setCity] = useState('')
  const [course, setCourse] = useState('')
  const [university, setUniversity] = useState('')
  const [instructor, setInstructor] = useState('')
  const [cellno, setCellno] = useState('')
  const [url, setUrl] = useState('')
  const [image, setImage] = useState('')
  async function addRecord(event) {
    event.preventDefault()
if(!url){
alert("Image not Selected")
}
else{
    const response = await fetch('http://localhost:1337/api/add', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        name,
        email,
        city,
        course,
        university,
        instructor,
        cellno,
        image: url
      }),
    })
    

    const data = await response.json()

    if (data.status === 'ok') {
      window.location.href = '/add'
    }
  }
  }


  const getdata = async () => {

    const res = await fetch("http://localhost:1337/add/getdata", {
      method: "GET",
      headers: {
        "Content-Type": "application/json"
      }
    });

    const data = await res.json();
    console.log(data);

    if (res.status === 422 || !data) {
      console.log("error ");

    } else {
      setUserdata(data)
      console.log("get data");

    }
  }

  useEffect(() => {
    getdata();
  }, [])

  const deleteuser = async (id) => {

    const res2 = await fetch(`http://localhost:1337/add/deleteuser/${id}`, {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json"
      }
    });

    const deletedata = await res2.json();
    console.log(deletedata);

    if (res2.status === 422 || !deletedata) {
      console.log("error");
    } else {
      console.log("user deleted");
      // setDLTdata(deletedata)
      getdata();
    }
  }
  const imagehandler = () => {

    const data = new FormData()
    data.append("upload_preset", "mern-crud")
    data.append("file", image)

    data.append("cloud_name", "dx6ro5dgg")

    //   const config = {
    //     method: "POST",
    //     body: data 
    // };
    //   var imgurl = "https://api.cloudinary.com/v1_1/dx6ro5dgg/image/upload";

    //   fetch(imgurl, config)
    //    .then(responseData => {
    //              console.log(JSON.stringify(responseData, null, 4));
    //    })

    fetch("https://api.cloudinary.com/v1_1/dx6ro5dgg/image/upload", {
      method: "post",
      body: data
    })
      .then(res => res.json())
      .then(data => {
        setUrl(data.url)
      })
      .catch(err => {
        console.log(err)
      })
  }
  function redirect() {
    Navigate('/')
  }

  return (

    <div>

      <div className="position-absolute w-100 min-height-300 top-0" style={{ backgroundImage: 'url(' + images['profile-layout-header.jpg'] + ')', backgroundPositionY: '50%' }}>
        <span className="mask bg-primary opacity-6" />
      </div>
      <aside className="sidenav bg-white navbar navbar-vertical navbar-expand-xs border-0 border-radius-xl my-3 fixed-start ms-4 " id="sidenav-main">
        <div className="sidenav-header">
          <i className="fas fa-times p-3 cursor-pointer text-secondary opacity-5 position-absolute end-0 top-0 d-none d-xl-none" aria-hidden="true" id="iconSidenav" />
          <a className="navbar-brand m-0" href="https://blssol.com/" target="_blank">
            <img src={images['logo-ct-dark.png']} className="navbar-brand-img h-100" alt="main_logo" />
            <span className="ms-1 font-weight-bold">Project</span>
          </a>
        </div>
        <hr className="horizontal dark mt-0" />
        <div className="collapse navbar-collapse  w-auto " id="sidenav-collapse-main">
          <ul className="navbar-nav">
            <li className="nav-item">
              <a className="nav-link" >
                <div className="icon icon-shape icon-sm border-radius-md text-center me-2 d-flex align-items-center justify-content-center">
                  <i className="ni ni-tv-2 text-primary text-sm opacity-10" />
                </div>
                <NavLink to="/Dashboard">Dashboard</NavLink>
              </a>
            </li>
            <li className="nav-item">
              <a className="nav-link active" >
                <div className="icon icon-shape icon-sm border-radius-md text-center me-2 d-flex align-items-center justify-content-center">
                  <i className="ni ni-calendar-grid-58 text-warning text-sm opacity-10" />
                </div>
                <NavLink to="/Add">Users</NavLink>
              </a>
            </li>
           
            <li className="nav-item mt-3">
              <h6 className="ps-4 ms-2 text-uppercase text-xs font-weight-bolder opacity-6">Account pages</h6>
            </li>
            <li className="nav-item">
              <a className="nav-link " href="./pages/profile.html">
                <div className="icon icon-shape icon-sm border-radius-md text-center me-2 d-flex align-items-center justify-content-center">
                  <i className="ni ni-single-02 text-dark text-sm opacity-10" />
                </div>
                <NavLink to="/Profile">Profile</NavLink>
              </a>
            </li>


          </ul>
        </div>
        <div className="sidenav-footer mx-3 ">
          <div className="card card-plain shadow-none" id="sidenavCard">
            <img className="w-50 mx-auto" src={images['icon-documentation.svg']} alt="sidebar_illustration" />
            <div className="card-body text-center p-3 w-100 pt-0">
              <div className="docs-info">
                <h6 className="mb-0">Need help?</h6>
                <p className="text-xs font-weight-bold mb-0">Please Visit <a style={{ color: '#5e72e4' }} href="www.blssol.com">BLS</a></p>
              </div>
            </div>
          </div>
          <a href target="_blank" className="btn btn-dark btn-sm w-100 mb-3">Documentation</a>
        </div>
      </aside>
      <main className="main-content position-relative border-radius-lg ">
        {/* Navbar */}
        <nav className="navbar navbar-main navbar-expand-lg px-0 mx-4 shadow-none border-radius-xl " id="navbarBlur" data-scroll="false">
          <div className="container-fluid py-1 px-3">
            <nav aria-label="breadcrumb">
              
              <h6 className="font-weight-bolder text-white mb-0">Users</h6>
            </nav>
            <div className="collapse navbar-collapse mt-sm-0 mt-2 me-md-0 me-sm-4" id="navbar">
              <div className="ms-md-auto pe-md-3 d-flex align-items-center">
              </div>
              <ul className="navbar-nav  justify-content-end">
                <li className="nav-item d-flex align-items-center">
                  <a href="javascript:;" className="nav-link text-white font-weight-bold px-0">
                    <i className="fa fa-user me-sm-1" />
                    <button style={{ color: "white", padding: "0", border: "none", background: "none" }} onClick={redirect} ><span className="d-sm-inline d-none">Log Out</span></button>
                  </a>
                </li>
                <li className="nav-item d-xl-none ps-3 d-flex align-items-center">
                  <a href="javascript:;" className="nav-link text-white p-0" id="iconNavbarSidenav">
                    <div className="sidenav-toggler-inner">
                      <i className="sidenav-toggler-line bg-white" />
                      <i className="sidenav-toggler-line bg-white" />
                      <i className="sidenav-toggler-line bg-white" />
                    </div>
                  </a>
                </li>
              </ul>
            </div>
          </div>
        </nav>
        {/* End Navbar */}
        <div className="card shadow-lg mx-4 card-profile-bottom">
          <div className="card-body p-3">
            <div className="row gx-4">
              <div className="col-auto">
                <div className="avatar avatar-xl position-relative">
                  <img src={images['db.jpg']} alt="profile_image" className="w-100 border-radius-lg shadow-sm" />
                </div>
              </div>
              <div className="col-auto my-auto">
                <div className="h-100">
                  <h5 className="mb-1">
                    Add Record Here
                  </h5>
                  <p className="mb-0 font-weight-bold text-sm">
                    student
                  </p>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="container-fluid py-4">
          <div className="row">
            <div className="col-md-8">
              <div className="card" style= {{width : "109%"}}>
                <div className="card-header pb-0">
                  <div className="d-flex align-items-center">

                    <table class="table">
                      <thead>
                        <tr className="table-dark">
                          <th>Profile</th>
                          <th>Name</th>
                          <th>Email</th>
                          <th>City</th>
                          <th>Course</th>
                          <th>University</th>
                          <th>Instructor</th>
                          <th>Cell</th>

                          <th>Actions</th>

                        </tr>
                      </thead>
                      <tbody>

                        {
                          getuserdata.map((element, id) => {
                            return (
                              <>
                                <tr>
                                  <td>  <div className="avatar avatar-xl position-relative">
                                    <img src={element.image} alt="profile_image" className="w-100 border-radius-lg shadow-sm" />
                                  </div></td>
                                 
                                  <td>{element.name}</td>
                                  <td>{element.email}</td>
                                  <td>{element.city}</td>
                                  <td>{element.course}</td>
                                  <td>{element.university}</td>
                                  <td>{element.instructor}</td>
                                  <td>{element.cellno}</td>

                                  <td class="align-middle text-center text-sm">
                                    {/* <img src={element.image}/> */}
                                    <button style={{ padding: "0", border: "none", background: "none" }} onClick={() => deleteuser(element._id)}><span style={{fontSize: "large"}}class="badge badge-sm bg-gradient-danger">Delete</span></button>
                                    <NavLink to={`edit/${element._id}`}> <button style={{ padding: "0", border: "none", background: "none" }}><span style={{fontSize: "large"}}class="badge badge-sm bg-gradient-success">Edit</span></button></NavLink>


                                  </td>

                                </tr>
                              </>
                            )
                          })
                        }
                      </tbody>
                    </table>
                  </div>
                </div>

              </div>
            </div>
            <div style = {{width: "26%" , marginLeft: "78px"}}className="col-md-4">
              <div className="card card-profile">

                
                <h1>Add Record</h1>


                <div class="form-group">
               
                  <input style={{marginLeft: "142px"}}type="file" onChange={(e) => setImage(e.target.files[0])} accept="image/*" />
                  

                  <button style= {{marginLeft: "137px" , marginTop : "12px"}}className="btn btn-success"type="submit" onClick={() => imagehandler()} >Image upload</button>
                  <span style = {{ marginBottom: "7px" , marginLeft: "107px"}}>Files Allowed <span style = {{color : "red" }} > (jpeg,jpg,png,svg)</span> </span>
                </div>
                <div className="box p-3 mb-3 mt-5" >
     
                  <form style = {{marginTop: "-45px" }} onSubmit={addRecord}>

                    <div class="form-group">
                      <input type="text" class="form-control  mb-4" name="name" value={name} onChange={(e) => setName(e.target.value)} placeholder="Enter name" required/>
                    </div>

                    <div class="form-group">
                      <input type="email" class="form-control  mb-4" name="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Enter Email" required/>
                    </div>

                    <div class="form-group">
                      <input type="text" class="form-control mb-4" name="city" value={city} onChange={(e) => setCity(e.target.value)} placeholder="Enter Your City" required/>
                    </div>

                    <div class="form-group">
                      <input type="text" class="form-control mb-4" name="course" value={course} onChange={(e) => setCourse(e.target.value)} placeholder="Enter Course Name" required/>
                    </div>

                    <div class="form-group">
                      <input type="text" class="form-control mb-2" name="university" value={university} onChange={(e) => setUniversity(e.target.value)} placeholder="Enter University Name" required/>
                    </div>
                    <div class="form-group">
                      <input type="text" class="form-control mb-2" name="instructor" value={instructor} onChange={(e) => setInstructor(e.target.value)} placeholder="Enter Instructor Name" required/>
                    </div>
                    <div class="form-group">
                      <input type="tel" maxlength="10"  pattern="[0-9]{4}-[0-9]{5}"   class="form-control mb-2" name="cellno" value={cellno} onChange={(e) => setCellno(e.target.value)} placeholder="Enter Cell No =1234-56789" required/>
                    </div>


                    <button type="submit" class="btn btn-primary btn-block mt-4">Insert Record</button>
                  </form>
                </div>

              </div>
            </div>
          </div>
          <footer className="footer pt-3  ">
            <div className="container-fluid">
              <div className="row align-items-center justify-content-lg-between">
                <div className="col-lg-6 mb-lg-0 mb-4">
                  <div className="copyright text-center text-sm text-muted text-lg-start">
                    
                    made with <a style={{ color: '#5e72e4' }} href="https://mui.com/"><strong>MUI</strong></a> by
                    <a href="https://www.creative-tim.com" className="font-weight-bold" target="_blank"> BLS</a>

                  </div>
                </div>
              </div>
            </div></footer>
        </div>
        {/* Github buttons */}
        {/* Control Center for Soft Dashboard: parallax effects, scripts for the example pages etc */}
      </main ></div >
  );
}
export default Add;
